/**
 * Signed decimal degrees without compass direction.
 */
interface Coordinates {
    lat: number;
    long: number;
}
/**
 * List of all supported units.
 *
 * m - metre
 * M - mile
 * km - kilometre
 * ft - foot
 */
declare const SUPPORTED_UNIT: {
    m: string;
    M: string;
    km: string;
    ft: string;
};
/**
 * Earth radius = 6 371 km.
 */
declare const R = 6371000;
/**
 * Degrees to radians.
 */
declare const degToRad: (deg: number) => number;
/**
 * Calculate the distance between two coordinates to metres.
 */
declare const calculateDistance: (coordinates1: Coordinates, coordinates2: Coordinates) => number;
declare const instanceOfCoordinates: (object: any) => object is Coordinates;
/**
 * Calculate the distance between two coordinates to the given unit.
 * Default unit is metre.
 */
declare const calculate: (coordinates1: Coordinates, coordinates2: Coordinates, unit?: string) => number;
